mapper.select:
select a.id, a.alarm_code, a.alarm_source, a.alarm_description, a.alarm_location, a.start_time, a.end_time, a.alarm_level, a.fault_type, a.manufacturers, a.device_code, a.device_name, a.wind_farm, a.alarm_status, a.asset_number, a.operating_status, a.create_time, a.update_time, a.tenant_code from fault_information a where 1=1
and a.tenant_code = #{bean.tenantCode}
and a.alarm_source like '%${bean.alarmSource}%'
and a.alarm_description like '%${bean.alarmDescription}%'
and a.alarm_location = #{bean.alarmLocation}
and a.alarm_level = #{bean.alarmLevel}
and a.fault_type = #{bean.faultType}
and a.asset_number = #{bean.assetNumber}
and a.alarm_status = #{bean.alarmStatus}
and a.operating_status = #{bean.operatingStatus}
and a.start_time between #{bean.startTime} and #{bean.endTime}
order by a.start_time desc

select a.id, a.alarm_code, a.alarm_source, a.alarm_description, a.alarm_location, a.start_time, a.end_time, a.alarm_level, a.fault_type, a.manufacturers, a.device_code, a.device_name, a.wind_farm, a.alarm_status, a.asset_number, a.operating_status, a.create_time, a.update_time, a.tenant_code from fault_information a where 1=1
and a.id = #{bean.id}
and a.tenant_code = #{bean.tenantCode}
and a.alarm_source like '%${bean.alarmSource}%'
and a.alarm_description like '%${bean.alarmDescription}%'
and a.alarm_location = #{bean.alarmLocation}
and a.alarm_level = #{bean.alarmLevel}
and a.fault_type = #{bean.faultType}
and a.asset_number = #{bean.assetNumber}
and a.alarm_status = #{bean.alarmStatus}
and a.operating_status = #{bean.operatingStatus}
and a.start_time between #{bean.startTime} and #{bean.endTime}
order by a.start_time desc

select f1.device_code, f1.device_name, IFNULL(f2.alarm_status,0) as untreated, IFNULL(f2.fault_type_day,0) as fault_type_day, IFNULL(f2.fault_type_mouth,0) as fault_type_mouth, IFNULL(f2.fault_type_all,0) as fault_type_all, IFNULL(f2.start_time,0) as recent_time from key_position f1 LEFT JOIN ( select d1.device_code, d1.alarm_status, d2.fault_type_day, d3.fault_type_mouth, d4.fault_type_all ,d5.start_time from (select device_code,count(*) as alarm_status from fault_information where 1=1
and tenant_code = #{bean.tenantCode}
and alarm_status=0 group by device_code,device_name ) d1, (select device_code,device_name,count(*) as fault_type_day from fault_information where 1=1
and tenant_code = #{bean.tenantCode}
and to_days(start_time) = to_days(now()) group by device_code,device_name ) d2, (select device_code,device_name,count(*) as fault_type_mouth from fault_information where 1=1
and tenant_code = #{bean.tenantCode}
and DATE_FORMAT( start_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' ) group by device_code,device_name ) d3, (select device_code,device_name,count(*) as fault_type_all from fault_information where 1=1
group by device_code,device_name ) d4, (select a.device_code,a.device_name, CASE WHEN a.start_time <= 60 THEN concat(start_time ,'分钟') WHEN a.start_time>60 and a.start_time <= 1440 THEN concat( TRUNCATE(start_time/60,0),' 小时') WHEN a.start_time>1440 and a.start_time <= 43200 THEN concat( TRUNCATE(start_time/1440,0),' 天') ELSE '大于一个月' END 'start_time' from ( select device_code,device_name,TIMESTAMPDIFF(MINUTE,max(start_time),now() ) as start_time from fault_information where 1=1
and tenant_code = #{bean.tenantCode}
group by device_code,device_name ) a ) d5 where d1.device_code=d2.device_code and d2.device_code=d3.device_code and d3.device_code = d4.device_code and d4.device_code = d5.device_code ) f2 on f1.device_code=f2.device_code where 1=1
and f1.tenant_code = #{bean.tenantCode}
order by f1.device_code
