mapper.select:
select a.id, a.alarm_code, a.alarm_source, a.alarm_description, a.alarm_location, a.start_time, a.end_time, a.incident_type, a.alarm_level, a.fault_type, a.manufacturers, a.device_code, a.device_name, a.wind_farm, a.asset_number, a.order_number, a.alarm_status, a.operating_status, a.discoverer_id, a.discoverer_name, a.remark, a.create_time, a.create_user_id, a.create_user_name, a.update_time, a.update_user_id, a.update_user_name, a.tenant_domain, b.filePath from fault_information a left join ( select pid,group_concat(file_path separator ',') as filePath from fault_file_record group by pid ) b on a.id=b.pid where a.incident_type=2
and a.tenant_domain=#{bean.tenantDomain}
and a.wind_farm in (${bean.windFarm})
and a.alarm_code=#{bean.alarmCode}
and a.alarm_source=#{bean.alarmSource}
and a.alarm_description like '%${bean.alarmDescription}%'
and a.alarm_location like '%${bean.alarmLocation}%'
and a.alarm_level=#{bean.alarmLevel}
and a.alarm_status =#{bean.alarmStatus}
and a.fault_type =#{bean.faultType}
and a.device_code like '%${bean.deviceCode}%'
and a.asset_number like '%${bean.assetNumber}%'
and a.operating_status =#{bean.operatingStatus}
and a.discoverer_id like '%${bean.discovererId}%'
and a.start_time between #{bean.sTime} and #{bean.eTime}
order by a.start_time desc
