mapper.select:
select a.id, a.alarm_code, a.alarm_source, a.alarm_description, a.alarm_location, a.start_time, a.end_time, a.alarm_level, a.fault_type, a.manufacturers, a.device_code, a.device_name, a.wind_farm, a.alarm_status, a.asset_number, a.operating_status, a.create_time, a.update_time, a.tenant_domain from fault_information a where a.incident_type=1
and a.tenant_domain = #{bean.tenantDomain}
and find_in_set(a.wind_farm, #{bean.windFarm})
and a.alarm_source like '%${bean.alarmSource}%'
and a.alarm_description like '%${bean.alarmDescription}%'
and a.alarm_location = #{bean.alarmLocation}
and a.alarm_level = #{bean.alarmLevel}
and a.fault_type = #{bean.faultType}
and a.asset_number = #{bean.assetNumber}
and a.alarm_status = #{bean.alarmStatus}
and a.operating_status = #{bean.operatingStatus}
and a.start_time between #{bean.startTime} and #{bean.endTime}
order by a.start_time desc

select a.id, a.alarm_code, a.alarm_source, a.alarm_description, a.alarm_location, a.start_time, a.end_time, a.alarm_level, a.fault_type, a.manufacturers, a.device_code, a.device_name, a.wind_farm, a.alarm_status, a.asset_number, a.operating_status, a.create_time, a.update_time, a.tenant_domain from fault_information a where a.incident_type=1
and a.id = #{bean.id}
and a.tenant_domain = #{bean.tenantDomain}
and find_in_set(a.wind_farm, #{bean.windFarm})
and a.alarm_source like '%${bean.alarmSource}%'
and a.alarm_description like '%${bean.alarmDescription}%'
and a.alarm_location = #{bean.alarmLocation}
and a.alarm_level = #{bean.alarmLevel}
and a.fault_type = #{bean.faultType}
and a.asset_number = #{bean.assetNumber}
and a.alarm_status = #{bean.alarmStatus}
and a.operating_status = #{bean.operatingStatus}
and a.start_time between #{bean.startTime} and #{bean.endTime}
order by a.start_time desc

SELECT f1.asset_number, f1.device_name, f1.STATUS, IFNULL(d4.fault_type_all, 0) AS fault_type_all, IFNULL(d2.fault_type_day, 0) AS fault_type_day, IFNULL(d3.fault_type_mouth, 0) AS fault_type_mouth, IFNULL(d5.start_time, 0) AS recent_time, IFNULL(d1.untreated, 0) AS untreated FROM key_position f1 LEFT JOIN ( -- 累计报警 SELECT asset_number, device_name, count(*) AS fault_type_all FROM fault_information WHERE incident_type = 1
and tenant_domain = #{bean.tenantDomain}
and find_in_set(wind_farm, #{bean.windFarm})
GROUP BY asset_number, device_name ) d4 on f1.asset_number=d4.asset_number and f1.device_name=d4.device_name left join ( -- 今日报警 SELECT asset_number, device_name, count(*) AS fault_type_day FROM fault_information WHERE incident_type = 1
and tenant_domain = #{bean.tenantDomain}
and find_in_set(wind_farm, #{bean.windFarm})
AND to_days(start_time) = to_days(now()) GROUP BY asset_number, device_name
) d2 on f1.asset_number=d2.asset_number and f1.device_name=d2.device_name left join ( -- 本月 SELECT asset_number, device_name, count(*) AS fault_type_mouth FROM fault_information WHERE incident_type = 1
and tenant_domain = #{bean.tenantDomain}
and find_in_set(wind_farm, #{bean.windFarm})
AND DATE_FORMAT(start_time, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m') GROUP BY asset_number, device_name
) d3 on f1.asset_number=d3.asset_number and f1.device_name=d3.device_name left join ( -- 最近时间 SELECT a.asset_number, a.device_name, CASE WHEN a.start_time >= 0 and a.start_time < 60 THEN concat(start_time ,'分钟') WHEN a.start_time >= 60 and a.start_time < 1440 THEN concat( TRUNCATE(start_time/60,0),' 小时') WHEN a.start_time >= 1440 and a.start_time < 43200 THEN concat( TRUNCATE(start_time/1440,0),' 天') ELSE '大于一个月' END 'start_time' FROM ( SELECT asset_number, device_name, TIMESTAMPDIFF( MINUTE, max(start_time), now() ) AS start_time FROM fault_information WHERE incident_type = 1
and tenant_domain = #{bean.tenantDomain}
and find_in_set(wind_farm, #{bean.windFarm})
GROUP BY asset_number, device_name ) a ) d5 on f1.asset_number=d5.asset_number and f1.device_name=d5.device_name left join ( -- 未处理 SELECT asset_number, device_name, count(*) AS untreated FROM fault_information WHERE incident_type = 1
and tenant_domain = #{bean.tenantDomain}
and find_in_set(wind_farm, #{bean.windFarm})
AND alarm_status = 0 GROUP BY asset_number, device_name ) d1 on f1.asset_number=d1.asset_number and f1.device_name=d1.device_name where 1=1
and f1.tenant_domain = #{bean.tenantDomain}
and find_in_set(f1.wind_farm, #{bean.windFarm})
order by f1.asset_number

select b.id, b.alarm_code, b.alarm_source, b.alarm_description, b.alarm_location, b.start_time, b.end_time, b.alarm_level, b.fault_type, b.manufacturers, b.device_code, b.device_name, b.wind_farm, b.alarm_status, b.asset_number, b.operating_status, b.create_time, b.update_time, b.tenant_domain from key_position a left join fault_information b on a.asset_number=b.asset_number where b.incident_type=1
and b.tenant_domain = #{bean.tenantDomain}
and find_in_set(b.wind_farm, #{bean.windFarm})
and a.device_code = #{bean.deviceCode}
and a.device_name like '%${bean.deviceName}%'
order by b.start_time desc
