mapper.select:
select id, model, protocol_no, iec_path, fault_code, fault_name, eng_name, component_name, pms_am_id, pms_ai_id, remark, status, enable, checker, check_time, create_time, create_user_id, create_user_name, tenant_code from fault_code_base where pms_am_id=#{bean.pmsAmId}
and pms_ai_id=#{bean.pmsAiId}
and tenant_code=#{bean.tenantCode}

mapper.delete:
	    delete 
	       from fault_code_base  
	    where pms_am_id=#{bean.pmsAmId} 

          and pms_ai_id=#{bean.pmsAiId}


          and tenant_code=#{bean.tenantCode}

	    delete 
	       from fault_solution_info  
	    where fault_code=#{bean.faultCode}

          and tenant_code=#{bean.tenantCode}
