mapper.select:
select id, pid, uid, sort, org_code, org_name, org_name as label, org_type, remark, wfmark, wind_farm, tenant_id, enable, level from sys_organization where 1=1
and tenant_id = #{bean.tenantId}
and org_code like '%${bean.orgCode}%'
and org_name like '%${bean.orgName}%'
and enable=#{bean.enable}
and remark like '%${bean.remark}%'
and create_time between #{bean.startTime} and #{bean.endTime}
order by sort asc

        select 
		   id,user_id,post_id,tenant_id
		from sys_user_post where post_id in (
		   select id from sys_post where organization_id= #{bean.id}
		)

select b.id, b.pid, b.uid, b.sort, b.org_code, b.org_name, b.org_type from sys_user_organization a ,sys_organization b where a.organization_id=b.id and b.enable = 0
and a.user_id = #{bean.id}
and b.tenant_id = #{bean.tenantId}

select a.id, a.pid, a.uid, a.sort, a.org_code, a.org_name, a.org_type, a.wfmark, a.remark, a.tenant_id, a.wind_farm, a.enable, a.level, a.create_time from sys_organization a ,sys_user_organization b where a.id=b.organization_id and a.tenant_id=b.tenant_id and a.org_type=1 and a.enable=0 and b.tenant_id=#{bean.tenantId}
and b.user_id=#{bean.userId}
order by sort asc
