mapper.delete:
    delete from sys_resource where id in (
	   select id from ( select id from sys_resource where fid= #{bean.id} ) h
	)

mapper.select:
select id, pid, uid, sort, level, res_code, res_name, res_type, res_href, permission, remark, tenant_id, enable from sys_resource where 1=1
and tenant_id= #{bean.tenantId}
and id= #{bean.id} or pid= #{bean.id}
UNION ALL
select (-a.id) as id, b.pid, a.res_id as uid, b.sort, (b.level+1) as level, a.permission_code as res_code, c.act_name as res_name, (b.res_type+1) as res_type, b.res_href, b.permission, b.remark, b.tenant_id, b.enable from sys_permission a left join sys_resource b on a.res_id=b.id left join sys_user_action c on a.act_id = c.id where a.tenant_id=b.tenant_id and a.tenant_id=c.tenant_id
and a.tenant_id= #{bean.tenantId}
and b.id= #{bean.id} or b.pid= #{bean.id}
