mapper.select:
select id, user_code, login_code, user_name, email, mobile, phone, sex, avatar, sign, wx_openid, qq_openid, address, remark, tenant_id, status, mgr_type, pwd_question, pwd_question_answer, pwd_question2, pwd_question_answer2, pwd_question3, pwd_question_answer3, enable, create_time, create_user_id, create_user_name, update_time, update_user_id, update_user_name from sys_user_info
and user_code=#{bean.userCode}
and login_code=#{bean.loginCode}
and user_name=#{bean.userName}
and mobile like '%${bean.mobile}%'
and enable=#{bean.enable}
and status=#{bean.status}
and mgr_type <= #{bean.mgrType}
and tenant_id = #{bean.tenantId}
order by create_time desc

select a.id, a.user_code, a.login_code, a.user_name, a.email, a.mobile, a.phone, a.sex, a.avatar, a.sign, a.wx_openid, a.qq_openid, a.address,mgr_type, a.remark, a.tenant_id, b.tenant_code, b.tenant_name, b.tenant_pw, b.tenant_domain, b.logo_url, b.sel from sys_user_info a left join sys_tenant b on a.tenant_id=b.id where a.enable=0 and b.enable=0
and a.user_code = #{bean.userCode}
and a.tenant_id = #{bean.tenantId}

        select 
			id,
			user_name as userName
		from sys_user_info where id in 

	        #{item}


	        and tenant_id=#{tenantId}

select f.id,f.userName from ( select c.id,c.user_name as userName from sys_user_organization a ,( select f1.id from ( select #{orgid} as id from sys_organization limit 0,1 ) f1 UNION all ( select id from ( select t1.id, if(find_in_set(uid, @pids) > 0, @pids := concat(@pids, ',', id), 0) as ischild from ( select id,uid from sys_organization t where 1=1 order by uid, id ) t1, (select @pids := #{orgid} ) t2 ) t3 where ischild != 0 ) ) b,sys_user_info c where a.organization_id = b.id and c.id=a.user_id
and c.tenant_id=#{tenantId}
) f group by f.id
