mapper.select:
select id, user_code, login_code, user_name, pwd, email, mobile, phone, sex, avatar, sign, wx_openid, qq_openid, address, remark, tenant_id, status, mgr_type, pwd_question, pwd_question_answer, pwd_question2, pwd_question_answer2, pwd_question3, pwd_question_answer3, enable, create_time, create_user_id, create_user_name, update_time, update_user_id, update_user_name from sys_user_info
and user_code=#{bean.userCode}
and login_code=#{bean.loginCode}
and user_name=#{bean.userName}
and mobile like '%${bean.mobile}%'
and enable=#{bean.enable}
and status=#{bean.status}
order by create_time desc
