mapper.select:
select b.id, b.tenant_id, b.tenant_domain, b.wind_farm, b.classify_name, b.svg, b.remark, b.create_time, a.org_name as windFarmName, ( select tenant_name from sys_tenant where enable =0 and id=b.tenant_id ) as tenantName from sys_organization a left join basic_asset_classification b on a.id =b.wind_farm where a.tenant_id=b.tenant_id and a.org_type=1
and b.tenant_id=#{bean.tenantId}
and b.tenant_domain=#{bean.tenantDomain}

select b.id, b.tenant_id, b.tenant_domain, b.wind_farm, b.classify_name, b.svg, b.remark, b.create_time, a.org_name as windFarmName, ( select tenant_name from sys_tenant where enable =0 and id=b.tenant_id ) as tenantName from sys_organization a left join basic_asset_classification b on a.id =b.wind_farm where a.tenant_id=b.tenant_id and a.org_type=1 and b.id=#{bean.id}
and b.tenant_id=#{bean.tenantId}
and b.tenant_domain=#{bean.tenantDomain}
