mapper.select:
select b.id, b.tenant_id, b.tenant_domain, b.wind_farm, b.route_address, b.secret_path, b.create_time, a.org_name as windFarmName from sys_organization a left join basic_iot_config b on a.id =b.wind_farm where a.tenant_id=b.tenant_id and a.org_type=1
and a.tenant_id=#{bean.tenantId}
and a.tenant_domain=#{bean.tenantDomain}

select b.id, b.tenant_id, b.tenant_domain, b.wind_farm, b.route_address, b.secret_path, b.create_time, a.org_name as windFarmName from sys_organization a left join basic_iot_config b on a.id =b.wind_farm where a.tenant_id=b.tenant_id and a.org_type=1 and b.id=#{bean.tenantId}
and a.tenant_id=#{bean.tenantId}
and a.tenant_domain=#{bean.tenantDomain}
