mapper.select:
select b.id, b.tenant_id, b.tenant_domain, b.wind_farm, b.service_address, b.status, b.msg, b.remark, b.create_time, b.end_time, a.org_name as windFarmName, ( select tenant_name from sys_tenant where enable =0 and id=b.tenant_id ) as tenantName from sys_organization a , basic_maint_notification b where a.id =b.wind_farm and a.tenant_id=b.tenant_id and a.org_type=1
and b.tenant_id=#{bean.tenantId}
and b.tenant_domain=#{bean.tenantDomain}
order by b.create_time desc

select b.id, b.tenant_id, b.tenant_domain, b.wind_farm, b.service_address, b.status, b.msg, b.remark, b.create_time, b.end_time, a.org_name as windFarmName, ( select tenant_name from sys_tenant where enable =0 and id=b.tenant_id ) as tenantName from sys_organization a , basic_maint_notification b where a.id =b.wind_farm and a.tenant_id=b.tenant_id and a.org_type=1 and b.id=#{bean.id}
and b.tenant_id=#{bean.tenantId}
and b.tenant_domain=#{bean.tenantDomain}
